import Layout from "../components/layout";
import fetch from "isomorphic-unfetch";
import Link from "next/link";
import Router from "next/router";

const Blog = ({ posts }) => (
  <Layout>
    <article id="blog-container">
      {posts.map(post => (
        <section key={post.slug} id="post-list">
          <h1>
            <Link href={post.slug}>
              <a>{post.title}</a>
            </Link>
          </h1>
          {post.details && <p>{post.details}</p>}
          <p>Post's date = {post.date}</p>
          <button
            onClick={() => Router.push(`/blog/?page=${5 - 1}`)}
            disabled={5 <= 1}
          ></button>
        </section>
      ))}
    </article>
    <style jsx>{`
      #post-list {
        color: white;
        border: 2px solid white;
        border-radius: 28%;
        text-align: center;
        margin: 1% 10%;
        padding: 1% 0;
        background-color: transparent;
      }
      a {
        font-weight: bold;
      }
      h1 {
        text-decoration: underline;
      }
      h1,
      p {
        color: white;
      }
    `}</style>
  </Layout>
);

Blog.getInitialProps = async ({ query: { page = 1 } }) => {
  const res = await fetch(`http://localhost:3000/api/posts/`);
  const data = await res.json();

  console.log(data);
  return { posts: data.posts, page: page }; //returns an array
};

export default Blog;
