import addData from "./dbCon";
import Layout from "../components/layout";

const transferData = data => {
  let name = data.blogName;
  let detail = data.blogDetail;

  addData({ blogName: name, blogDetail: detail });
};

const Mock = () => (
  <Layout>
    <h1 style={{ color: "white" }}>Deneme</h1>
    <input type="text" placeholder="Blog Name" id="inputName" />

    <br />
    <br />
    <input type="text" placeholder="Blog Detail" id="inputDetail" />

    <br />
    <br />

    <button
      className="submit"
      onClick={e =>
        addData({
          blogName: document.getElementById("inputName").value,
          blogDetail: document.getElementById("inputDetail").value
        })
      }
    >
      Submit
    </button>

    <p style={{ color: "red" }} hidden>
      Blank input!
    </p>
  </Layout>
);

export default Mock;
