import Layout from "../components/layout";
import fetch from "isomorphic-unfetch";
import Router from "next/router";
import Link from "next/link";
import ReactMarkdown from "react-markdown";
import { authInstance, checkUser } from "../src/dbCon";

export const changeUserState = user => {
  if (user) {
    document
      .querySelectorAll(".sign-out")
      .forEach(item => item.classList.add("d-none"));
    document
      .querySelectorAll(".sign-in")
      .forEach(item => item.classList.remove("d-none"));
  }
  //if logged out
  else {
    document
      .querySelectorAll(".sign-out")
      .forEach(item => item.classList.remove("d-none"));
    document
      .querySelectorAll(".sign-in")
      .forEach(item => item.classList.add("d-none"));
  }
};

const signinModal = () => (
  <div
    className="modal fade"
    id="signinModal"
    tabIndex="-1"
    role="dialog"
    aria-labelledby="signinModalLabel"
    aria-hidden="true"
  >
    <div className="modal-dialog" role="document">
      <div className="modal-content">
        <div className="modal-header">
          <h5 className="modal-title" id="signinModalLabel">
            Sign In
          </h5>
          <button
            type="button"
            className="close"
            data-dismiss="modal"
            aria-label="Close"
          >
            <span aria-hidden="true">&times;</span>
          </button>
        </div>
        <div className="modal-body">
          <input type="text" placeholder="email" id="emailSignin" />
          <br />
          <br />

          <input type="password" placeholder="password" id="passSignin" />
          <p id="statusSignin"></p>
        </div>
        <div className="modal-footer">
          <button
            type="button"
            className="btn btn-secondary signin-close-button"
            data-dismiss="modal"
          >
            Close
          </button>
          <button
            type="button"
            className="btn btn-primary"
            onClick={() => {
              document.getElementById("statusSignin").innerHTML =
                "Checking credentials...";
              authInstance()
                .signInWithEmailAndPassword(
                  document.getElementById("emailSignin").value,
                  document.getElementById("passSignin").value
                )
                .then(() => {
                  document.getElementById("passSignin").value = "";
                  document.getElementById("emailSignin").value = "";
                  changeUserState();
                  Router.push("/blog");
                  document.querySelector(".signin-close-button").click();
                  // window.location.reload();
                })
                .catch(error => {
                  document.getElementById("statusSignin").innerHTML =
                    error.code;
                  console.log("Error code: ", error.code);
                  console.log("Error Message: ", error.message);
                });
            }}
          >
            Sign-in
          </button>
        </div>
      </div>
    </div>
  </div>
);

const registerModal = () => (
  <div
    className="modal fade"
    id="registerModal"
    tabIndex="-1"
    role="dialog"
    aria-labelledby="registerModalLabel"
    aria-hidden="true"
  >
    <div className="modal-dialog" role="document">
      <div className="modal-content">
        <div className="modal-header">
          <h5 className="modal-title" id="registerModalLabel">
            Register
          </h5>
          <button
            type="button"
            className="close"
            data-dismiss="modal"
            aria-label="Close"
          >
            <span aria-hidden="true">&times;</span>
          </button>
        </div>
        <div className="modal-body">
          <input type="text" placeholder="email" id="emailRegister" />
          <br />
          <br />

          <input type="password" placeholder="password" id="passRegister" />
          <p id="statusRegister" style={{ marginTop: "3%" }}></p>
        </div>
        <div className="modal-footer">
          <button
            type="button"
            className="btn btn-secondary register-close-button"
            data-dismiss="modal"
          >
            Close
          </button>
          <button
            type="button"
            className="btn btn-primary"
            onClick={() => {
              authInstance()
                .createUserWithEmailAndPassword(
                  document.getElementById("emailRegister").value,
                  document.getElementById("passRegister").value
                )
                .then(() => {
                  console.log("Registered.");
                  document.getElementById("statusRegister").innerHTML =
                    "Registered.";
                  Router.push("/blog");
                  document.querySelector(".register-close-button").click();
                  // authInstance().signOut();
                  // window.location.reload();
                })
                .catch(function(error) {
                  console.log(error);
                  document.getElementById("statusRegister").innerHTML =
                    error.message;
                });
            }}
          >
            Register
          </button>
        </div>
      </div>
    </div>
  </div>
);

const loginBlogButtons = () => {
  return (
    <div className="container-fluid mt-2">
      <div className="row d-flex justify-content-end">
        <div className="col-lg-2 col-md-2 col-6 sign-in">
          <Link href="/admin">
            <button type="button" className="btn btn-primary btn-lg">
              Write
            </button>
          </Link>
        </div>
        <div className="col-lg-2 col-md-2 col-6 sign-in">
          <button
            type="button"
            className="btn btn-secondary btn-lg"
            onClick={() => {
              authInstance()
                .signOut()
                .then(() => window.location.reload())
                .catch(() => console.log("Sign-out problem."));
            }}
          >
            Sign-out
          </button>
        </div>
      </div>
    </div>
  );
};

const logoutBlogButtons = () => {
  return (
    <div className="container-fluid mt-2">
      <div className="row d-flex justify-content-end">
        <div className="col-lg-2 col-md-2 col-6 sign-out">
          <button
            type="button"
            className="btn btn-primary btn-lg"
