import Header from "./header";
import Footer from "./footer";
import Head from "next/head";
import Link from "next/link";

const Communication = () => (
  <div className="comm">
    <ul>
      <li>
        <Link href="https://www.linkedin.com/in/erin%C3%A7-polat-4bb7bb192/">
          <a>LinkedIn</a>
        </Link>
      </li>
      <li>
        <Link href="https://www.instagram.com/erincpolat">
          <a>Instagram</a>
        </Link>
      </li>
      <li>
        <Link href="https://github.com/epolat-github">
          <a>GitHub</a>
        </Link>
      </li>
      <li>
        <Link href="mailto:erinc.polat@gmail.com">
          <a>Mail</a>
        </Link>
      </li>
    </ul>
    <style jsx>{`
      .comm {
        style: flex;
        margin-top: 3%;
      }
      .comm ul {
        padding: 0px;
        text-align: center;
        list-style: none;
      }
      .comm li {
        margin: 1em 0em;
      }
      .comm a {
        color: white;
        text-decoration: none;
        font-size: 28px;
      }
      .comm a:hover {
        color: red;
      }
    `}</style>
  </div>
);

const Layout = props => (
  <div className="layout-container">
    <Head>
      <link
        href="https://fonts.googleapis.com/css?family=Roboto+Mono&display=swap"
        rel="stylesheet"
      />

      <link
        href="https://fonts.googleapis.com/css?family=Abril+Fatface&display=swap"
        rel="stylesheet"
      />

      <style>{`
    body {
      overflow-x: hidden;
      overflow-y: hidden;
      margin: 0;
      padding: 0;
      background-image: url("/background.jpg"); 
      background-image-repeat: repeat;
      // background-color: #0cbaba;
      // background-image: linear-gradient(315deg, #0cbaba 0%, #380036 74%);

    }
  `}</style>
    </Head>
    <Header />
    {props.children}
    <Communication />
    <style jsx>{`
      .layout-container {
        #background-color: #d6c698;
        #background-color: cyan;
        #background-color: rgb(10, 25, 47);
        margin: 0px;
        padding: 0px;
      }
    `}</style>
  </div>
);

export default Layout;
