import Layout from "../components/layout";
import fetch from "isomorphic-unfetch";
import Link from "next/link";
import ReactMarkdown from "react-markdown";

const Blog = ({ posts, postCount }) => (
  <Layout>
    <article id="blog-container">
      {posts &&
        posts.map(post => (
          <section key={post.slug} id="post-list">
            <h1>
              <Link href={post.slug}>
                <a>{post.title}</a>
              </Link>
            </h1>
            {post.details && <ReactMarkdown source={post.details} />}
            <p>Post's date = {post.date}</p>
          </section>
        ))}
      <div id="page-button-container">
        {[...Array(Math.ceil(postCount / 5))].map((curr, index) => (
          <button className="page-button">{index + 1}</button>
        ))}
      </div>
    </article>
    <style jsx>{`
      #post-list {
        color: white;
        border: 2px solid white;
        border-radius: 28%;
        text-align: center;
        margin: 1% 10%;
        padding: 1% 0;
        background-color: transparent;
      }
      a {
        font-weight: bold;
      }
      h1 {
        text-decoration: underline;
      }
      #page-button-container {
        position: relative;
        left: 30%;
      }
      .page-button {
        background-color: #4caf50; /* Green */
        border: none;
        color: white;
        padding: 15px 32px;
        text-align: center;
        text-decoration: none;
        display: inline-block;
        font-size: 16px;
        cursor: pointer;
        float: left;
      }
      .page-button:hover {
        background-color: #3e8e41;
      }
    `}</style>
  </Layout>
);

Blog.getInitialProps = async () => {
  const res = await fetch(`http://localhost:3000/api/posts`);
  const data = await res.json();

  return { posts: data.posts, postCount: parseInt(data.postCount, 10) }; //returns an array
};

export default Blog;
