import Layout from "../components/layout";
import Link from "next/link";

const Index = () => (
  <Layout>
    <article id="summaryText">
      <section>
        {/* <p>Erinç Polat = {"{"}</p>
        <p>
          Full-time: "learner", <br />
          Part-time: "student" <br />
        </p>
        <p>{"}"};</p> */}

        <p>Erinç Polat = {"{"} 
        <br />  Full-time: "learner",
        <br />  Part-time: "student"
        <br />  {"}"}
        </p>
        <img src="/photo.png" alt="picture" />

      </section>
    </article>
    <style jsx>{`
      #summaryText {
        max-width: 100%;
        #padding: 30% 30%;
      }
      #summaryText p {
        white-space: pre;
        font-family: 'Roboto Mono', monospace;
        //letter-spacing: 10px;
        #color: #4d4737;
        color: #a8ada9;
        text-align: center;
        font-size: 65px;
        font-weight: bold;
        line-height: 1.5em;
        margin-top: 7%;
      }
      #summaryText p:nth-child(2) {
        padding-top: 3%;
        padding-right: 10%;
      }
      #summaryText p:nth-child(3) {
        #padding-left: 30%;
      }
      #summaryText p:nth-child(4) {
        padding-left: 4%;
      }
      #summaryText img {
        filter: grayscale(20%);
        display: block;
        margin-left: auto;
        margin-right: auto;
        width: 30%;
        height: auto;
        max-width: 100%;
      }
      #summaryText img:hover {
        filter: grayscale(40%);
      }
    `}</style>
  </Layout>
);

export default Index;
