import Header from "../components/header";
import Layout from "../components/layout";
import Link from "next/link";

const Communication = () => (
  <div className="comm">
    <ul>
      <li>
        <Link href="https://www.linkedin.com/in/erin%C3%A7-polat-4bb7bb192/">
          <a>LinkedIn</a>
        </Link>
      </li>
      <li>
        <Link href="https://www.instagram.com/erincpolat">
          <a>Instagram</a>
        </Link>
      </li>
      <li>
        <Link href="https://github.com/epolat-github">
          <a>GitHub</a>
        </Link>
      </li>
      <li>
        <Link href="mailto:erinc.polat@gmail.com">
          <a>Mail</a>
        </Link>
      </li>
    </ul>
    <style jsx>{`
      .comm {
        margin-top: 3%;
      }
      .comm ul {
        padding: 0px;
        text-align: center;
        list-style: none;
      }
      .comm li {
        margin: 1em 0em;
      }
      .comm a {
        color: white;
        text-decoration: none;
        font-size: 28px;
      }
      .comm a:hover {
        color: red;
      }
    `}</style>
  </div>
);

const Index = () => (
  <Layout>
    <article id="summaryText">
      <section>
        <img src="/photo.png" alt="picture" />
        <p>Erinç Polat = {"{"}</p>
        <p>
          Full-time: "learner", <br />
          Part-time: "student" <br />
        </p>
        <p>{"}"};</p>
      </section>
      <Communication />
    </article>
    <style jsx>{`
      #summaryText {
        max-width: 100%;
        #padding: 30% 30%;
      }
      #summaryText p {
        font-family: "Bangers", cursive;
        letter-spacing: 10px;
        #color: #4d4737;
        color: #a8ada9;
        text-align: center;
        font-size: 80px;
        font-weight: bold;
        line-height: 1.5em;
        margin: 0px;
      }
      #summaryText p:nth-child(2) {
        padding-top: 3%;
        padding-right: 10%;
      }
      #summaryText p:nth-child(3) {
        #padding-left: 30%;
      }
      #summaryText p:nth-child(4) {
        padding-left: 4%;
      }
      #summaryText img {
        margin-top: 30px;
        display: block;
        margin-left: auto;
        margin-right: auto;
        width: 30%;
        height: auto;
        max-width: 100%;        
      }
    `}</style>
  </Layout>
);

export default Index;
