import Header from "../components/header";
import Layout from "../components/layout";
import Link from "next/link";

const Communication = () => (
  <div className="comm">
    <ul>
      <li>
        <Link href="https://www.linkedin.com/in/erin%C3%A7-polat-4bb7bb192/">
          <a>LinkedIn</a>
        </Link>
      </li>
      <li>
        <Link href="https://www.instagram.com/erincpolat">
          <a>Instagram</a>
        </Link>
      </li>
      <li>
        <Link href="https://github.com/epolat-github">
          <a>GitHub</a>
        </Link>
      </li>
      <li>
        <Link href="mailto:erinc.polat@gmail.com">
          <a>Mail</a>
        </Link>
      </li>
    </ul>
    <style jsx>{`
      .comm ul {

        padding-left: 50%;
        list-style: none;
      }
      .comm li {
        padding-bottom: 3em;

      }
      .comm a {

        color:white;
        text-decoration:none;
        font-size:28px;
      }
      .comm a:hover {
        color:red;
      }
    `}</style>
  </div>
);

const Index = () => (
  <Layout>
    <div className="index-container">
      <article id="summaryText">
        <section>
          <img src="/logo.png" alt="picture" />
          <p>Erinç Polat = {"{"}</p>
          <p>
            Full-time: "learner", <br />
            Part time: "student" <br />
          </p>
          <p>{"}"};</p>
        </section>
        <Communication />
      </article>
      <style jsx>{`
        #summaryText {
          background-color: #001f3f;
          #padding: 30% 30%;
        }
        #summaryText p {
          width: 1300px;
          color: white;
          font-size: 38px;
          font-weight: bold;
          text-align: left;
          line-height: 1.5em;
          margin: 0px;
        }
        #summaryText p:nth-child(3) {
          padding-left: 50%;
        }
        #summaryText p:nth-child(4) {
          padding-left: 70%;
        }
      `}</style>
    </div>
  </Layout>
);

export default Index;
