import addData from "../src/dbCon";
import { dbInstance } from "../src/dbCon";
import Layout from "../components/layout";

const transferData = data => {
  let slug = data.blogSlug;
  let name = data.blogName;
  let detail = data.blogDetail;
  document.querySelector(p).innerHTML(`name: ${name}, detail: ${detail}`);
  addData({ blogSlug: slug, blogName: name, blogDetail: detail });
};

function getData() {
  let firestore = dbInstance();
  firestore
    .collection("blogs")
    .get()
    .then(snapshot =>
      snapshot.forEach(doc => {
        return <p>{doc.id}</p>
    })
    )
    .catch(err => {
      console.log("Error occurred => ", err);
    });
}

// async function fillData(data) {
