import Layout from "../components/layout";
import fetch from "isomorphic-unfetch";
import Link from "next/link";
import ReactMarkdown from "react-markdown";

const pageButtons = postCount => {
  console.log(postCount);
  const pageCount = Math.ceil(postCount / 5);

  const output = [...Array(pageCount)].forEach(() => <li><p>deneme</p></li>)
};

const Blog = ({ posts, postCount }) => (
  <Layout>
    <article id="blog-container">
      {posts &&
        posts.map(post => (
          <section key={post.slug} id="post-list">
            <h1>
              <Link href={post.slug}>
                <a>{post.title}</a>
              </Link>
            </h1>
            {post.details && <ReactMarkdown source={post.details} />}
            <p>Post's date = {post.date}</p>
          </section>
        ))}
      {pageButtons(postCount)}
    </article>
    <style jsx>{`
      #post-list {
        color: white;
        border: 2px solid white;
        border-radius: 28%;
        text-align: center;
        margin: 1% 10%;
        padding: 1% 0;
        background-color: transparent;
      }
      a {
        font-weight: bold;
      }
      h1 {
        text-decoration: underline;
      }
    `}</style>
  </Layout>
);

Blog.getInitialProps = async () => {
  const res = await fetch(`http://localhost:3000/api/posts`);
  const data = await res.json();

  return { posts: data.posts, postCount: parseInt(data.postCount, 10) }; //returns an array
};

export default Blog;
