import addData from "../src/dbCon";
import { dbInstance } from "../src/dbCon";
import Layout from "../components/layout";

const transferData = data => {
  let slug = data.blogSlug;
  let name = data.blogName;
  let detail = data.blogDetail;
  document.querySelector(p).innerHTML(`name: ${name}, detail: ${detail}`);
  addData({ blogSlug: slug, blogName: name, blogDetail: detail });
};

async function getData() {
  let firestore = dbInstance();
  firestore
    .collection("blogs")
    .get()
    .then(snapshot => {
    //   console.log(snapshot);
      return snapshot;
    })
    .catch(err => {
      console.log("Error occurred => ", err);
    });
}
// snapshot.forEach(doc => {
//     console.log(doc);
//   return (
//     <p>
//       {doc.id} => {doc.data()}
//     </p>
//   );
// });
const fillData = data => {
  console.log(getData());
  let content = document.createTextNode(getData());
  document.querySelector("#data").appendChild(content);
};

const Mock = () => (
  <Layout>
    <h1 style={{ color: "white" }}>Deneme</h1>
    <input type="text" placeholder="Blog Slug" id="inputSlug" />
    <br />
    <br />
    <input type="text" placeholder="Blog Title" id="inputName" />
    <br />
    <br /> <input type="text" placeholder="Blog Detail" id="inputDetail" />
    <br />
    <br />
    <button
      className="submit"
      onClick={() =>
        addData({
          blogSlug: document.getElementById("inputSlug").value,
          blogName: document.getElementById("inputName").value,
          blogDetail: document.getElementById("inputDetail").value
        })
      }
    >
      Submit
    </button>
    <button onClick={() => fillData()}>Get</button>
    <div id="data"></div>
    <p style={{ color: "red" }}>Blank input!</p>
  </Layout>
);

export default Mock;
