import Layout from "../components/layout";
import fetch from "isomorphic-unfetch";
import Link from "next/link";
import ReactMarkdown from "react-markdown";
import Router from 'next/router';

let page = 1;
const router = Router();

function buttonHandler(direction) {
  if (direction === "next") {
    page++;
    router.replace("/");
  } else if (direction === "previous") {
    page--;
    router.replace("/");
  }
}

const Blog = ({ posts, postCount }) => (
  <Layout>
    <article id="blog-container">
      <h1>{page}</h1>
      {posts &&
        posts.slice((page - 1) * 5, page * 5).map(post => (
          <section key={post.slug} id="post-list">
            <h1>
              <Link href={post.slug}>
                <a>{post.title}</a>
              </Link>
            </h1>
            {post.details && <ReactMarkdown source={post.details} />}
            <p>Post's date = {post.date}</p>
          </section>
        ))}
      <div id="page-button-container">
        <button
          className="page-button"
          disabled={page <= 1}
          onClick={buttonHandler("previous")}
        >
          Previous
        </button>
        {[...Array(Math.ceil(postCount / 5))].map((curr, index) => (
          <button className="page-button">{index + 1}</button>
        ))}
        <button
          className="page-button"
          disable={page >= Math.ceil(postCount / 5)}
          onClick={buttonHandler("next")}
        >
          Next
        </button>
      </div>
    </article>
    <style jsx>{`
      #post-list {
        color: white;
        border: 2px solid white;
        border-radius: 28%;
        text-align: center;
        margin: 1% 10%;
        padding: 1% 0;
        background-color: transparent;
      }
      a {
        font-weight: bold;
      }
      h1 {
        text-decoration: underline;
      }
      #page-button-container {
        /*display: inline;
        position: relative;
        left: 40%; */
        display: block;
        max-width: 600px;
        margin: auto;
      }
      .page-button {
        background-color: #4caf50; /* Green */
        border: 1px solid white;
        border-radius: 50%;
        color: white;
        padding: 15px 32px;
        text-align: center;
        text-decoration: none;
        display: inline-block;
        font-size: 25px;
        cursor: pointer;
        float: left;
      }
      .page-button:hover {
        background-color: #3e8e41;
      }
    `}</style>
  </Layout>
);

Blog.getInitialProps = async ({ req, query }) => {
  const res = await fetch(`http://localhost:3000/api/posts`);
  const data = await res.json();

  return { posts: data.posts, postCount: parseInt(data.postCount, 10) }; //returns an array
};

export default Blog;
